#Variables
stringNumber=""
finalResult=1

#Loops
for index1 in range(len(stringNumber)-13): #stop 13 digits before end to prevent indexing out of range
    product=1
    for index2 in range(13):
        product*=int(stringNumber[index1+index2]) #index string and convert to int
    if product>finalResult:
        finalResult=product

print(finalResult)

